import React from "react";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { user } = useAuth();

  if (!user) {
    return (
      <div className="flex justify-center items-center min-h-screen text-red-500 text-lg">
         You are not logged in.
      </div>
    );
  }

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100 px-4">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md text-gray-800">
        <h2 className="text-3xl font-bold mb-6 text-center text-blue-700">My Profile</h2>
        <div className="space-y-4 text-base">
          <p><span className="font-semibold"> Name:</span> {user.name}</p>
          <p><span className="font-semibold"> Email:</span> {user.email}</p>
          <p><span className="font-semibold"> User ID:</span> {user.id}</p>
        </div>
        <div className="mt-8 text-sm text-center text-gray-500">
          This is your personal profile page.
        </div>
      </div>
    </div>
  );
};

export default Profile;
