import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axiosInstance from '../axiosConfig';

function TaskForm({ tasks, setTasks, editingTask, setEditingTask }) {
  const { user } = useAuth();
  const [form, setForm] = useState({
    title: '',
    description: '',
    deadline: '',
  });

  useEffect(() => {
    if (editingTask) {
      setForm({
        title: editingTask.title,
        description: editingTask.description,
        deadline: editingTask.deadline,
      });
    } else {
      setForm({ title: '', description: '', deadline: '' });
    }
  }, [editingTask]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!form.title.trim()) {
      alert('Please enter a task name.');
      return;
    }

    try {
      const headers = { Authorization: `Bearer ${user.token}` };
      if (editingTask) {
        const res = await axiosInstance.put(`/api/tasks/${editingTask._id}`, form, { headers });
        const updated = tasks.map((t) => (t._id === res.data._id ? res.data : t));
        setTasks(updated);
      } else {
        const res = await axiosInstance.post('/api/tasks', form, { headers });
        setTasks([...tasks, res.data]);
      }

      setForm({ title: '', description: '', deadline: '' });
      setEditingTask(null);
    } catch (err) {
      alert('Could not save task. Try again.');
    }
  }

  return (
  <div className="max-w-2xl mx-auto p-4">
    <form onSubmit={handleSubmit} className="mb-6 bg-white p-6 rounded shadow">
      <h2 className="text-xl font-semibold mb-4">
        {editingTask ? "Edit Task" : "Add New Task"}
      </h2>
      <input
        placeholder="Title"
        value={formData.title}
        onChange={(e) => setFormData({ ...formData, title: e.target.value })}
        className="w-full mb-3 p-2 border rounded"
      />
      <textarea
        placeholder="Description"
        value={formData.description}
        onChange={(e) =>
          setFormData({ ...formData, description: e.target.value })
        }
        className="w-full mb-3 p-2 border rounded"
      />
      <input
        type="date"
        value={formData.deadline}
        onChange={(e) =>
          setFormData({ ...formData, deadline: e.target.value })
        }
        className="w-full mb-4 p-2 border rounded"
      />
      <button className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
        {editingTask ? "Update Task" : "Add Task"}
      </button>
    </form>

    <div className="space-y-4">
      <h2 className="text-lg font-semibold mb-2">Your Tasks</h2>
      {tasks.length === 0 ? (
        <p>No tasks added yet.</p>
      ) : (
        tasks.map((task) => (
          <div
            key={task._id}
            className="bg-gray-100 p-4 rounded shadow flex justify-between items-start"
          >
            <div>
              <h3 className="font-semibold">{task.title}</h3>
              <p className="text-sm text-gray-700">{task.description}</p>
              <p className="text-xs text-gray-500">
                Deadline: {new Date(task.deadline).toLocaleDateString()}
              </p>
            </div>
            <div className="space-x-2">
              <button
                onClick={() => handleEdit(task)}
                className="text-blue-600 hover:underline"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(task._id)}
                className="text-red-600 hover:underline"
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  </div>
);

}

export default TaskForm;
