import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axiosInstance from '../axiosConfig';

function TaskForm({ tasks, setTasks, editingTask, setEditingTask }) {
  const { user } = useAuth();
  const [form, setForm] = useState({
    title: '',
    description: '',
    deadline: '',
  });

  useEffect(() => {
    if (editingTask) {
      setForm({
        title: editingTask.title,
        description: editingTask.description,
        deadline: editingTask.deadline,
      });
    } else {
      setForm({ title: '', description: '', deadline: '' });
    }
  }, [editingTask]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!form.title.trim()) {
      alert('Please enter a task name.');
      return;
    }

    try {
      const headers = { Authorization: `Bearer ${user.token}` };
      if (editingTask) {
        const res = await axiosInstance.put(`/api/tasks/${editingTask._id}`, form, { headers });
        const updated = tasks.map((t) => (t._id === res.data._id ? res.data : t));
        setTasks(updated);
      } else {
        const res = await axiosInstance.post('/api/tasks', form, { headers });
        setTasks([...tasks, res.data]);
      }

      setForm({ title: '', description: '', deadline: '' });
      setEditingTask(null);
    } catch (err) {
      alert('Could not save task. Try again.');
    }
  }

  return (
    <form onSubmit={handleSubmit} style={{ background: '#fff', padding: 20, borderRadius: 10 }}>
      <h2>{editingTask ? 'Edit Task' : 'New Task'}</h2>

      <input
        placeholder="Task Title"
        value={form.title}
        onChange={(e) => setForm({ ...form, title: e.target.value })}
        style={{ display: 'block', width: '100%', marginBottom: 10 }}
      />

      <textarea
        placeholder="Description"
        value={form.description}
        onChange={(e) => setForm({ ...form, description: e.target.value })}
        style={{ display: 'block', width: '100%', marginBottom: 10 }}
      />

      <input
        type="date"
        value={form.deadline}
        onChange={(e) => setForm({ ...form, deadline: e.target.value })}
        style={{ display: 'block', width: '100%', marginBottom: 10 }}
      />

      <button type="submit" style={{ padding: '10px 20px', background: 'blue', color: 'white' }}>
        {editingTask ? 'Update' : 'Add'}
      </button>
    </form>
  );
}

export default TaskForm;
